/subsystem=security/security-domain=ResourceServerRealm:add(cache-type=default)
/subsystem=security/security-domain=ResourceServerRealm/authentication=classic:add(login-modules=[{"code"=>"Database", "flag"=>"required", "module-options"=>[("dsJndiName"=>"java:/jboss/datasources/ResourceServerDS"),("principalsQuery"=>"select PASSWORD from RS_USER where EMAIL=?"), ("rolesQuery"=>"select role.ROLENAME, 'Roles' from RS_ROLE role inner join RS_USER user on user.ROLE_ID = role.ROLE_ID where user.EMAIL=?"),("hashAlgorithm"=>"SHA-256"),("hashEncoding"=>"base64"),("unauthenticatedIdentity"=>"guest")]}])
